    private void EntrarButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EntrarButtonActionPerformed
        try {
            String login, senha;

            login = LoginScanner.getText();
            senha = SenhaScanner.getText();

            FuncionarioDTO objfuncionario = new FuncionarioDTO();
            objfuncionario.setNome_funcionario(login);
            objfuncionario.setSenha_funcionario(senha);

            FuncionarioDAO objfuncionariodao = new FuncionarioDAO();
            ResultSet rsfuncionariodao = objfuncionariodao.autenticarLogin(objfuncionario);

            if (rsfuncionariodao.next()) {
                this.controller.Entrar();
            } else {
                JOptionPane.showMessageDialog(null, "Usu√°rio e senha incorretos!");
            }

        } catch (SQLException erro) {
            JOptionPane.showMessageDialog(null, "LoginViews" + erro);
        }

    }//GEN-LAST:event_EntrarButtonActionPerformed

